<?php\n\nnamespace App\\Http\\Controllers;\n\nuse Illuminate\\Http\\Request;\nuse App\\Models\\Offer;\n\nclass FlashOfferDebugController extends Controller\n{\n    public function index()\n    {\n        // Get active flash offer\n        $activeFlashOffer = Offer::where('is_flash_offer', true)\n            ->where('is_active', true)\n            ->where('show_popup', true)\n            ->where('start_date', '<=', now())\n            ->where('end_date', '>=', now())\n            ->first();\n        \n        // Get all flash offers\n        $allFlashOffers = Offer::where('is_flash_offer', true)\n            ->orderBy('created_at', 'desc')\n            ->get();\n        \n        return view('flash-offer-debug', compact('activeFlashOffer', 'allFlashOffers'));\n    }\n}\n", "oldText": ""}]