<?php\n\n/**\n * Quick status check for minimum order validation system\n */\n\nrequire_once __DIR__ . '/vendor/autoload.php';\n\necho \"\\n\" . str_repeat('=', 50) . \"\\n\";\necho \"MINIMUM ORDER VALIDATION - STATUS CHECK\\n\";\necho str_repeat('=', 50) . \"\\n\\n\";\n\ntry {\n    // Check if settings exist\n    $enabled = \\App\\Models\\AppSetting::get('min_order_validation_enabled', null);\n    $amount = \\App\\Models\\AppSetting::get('min_order_amount', null);\n    $message = \\App\\Models\\AppSetting::get('min_order_message', null);\n    \n    echo \"📊 CURRENT CONFIGURATION:\\n\";\n    echo \"   Validation Enabled: \" . ($enabled ? 'YES ✅' : 'NO ❌') . \"\\n\";\n    echo \"   Minimum Amount: Rs \" . ($amount ?? 'NOT SET') . \"\\n\";\n    echo \"   Custom Message: \" . ($message ? 'SET ✅' : 'NOT SET ❌') . \"\\n\\n\";\n    \n    // Test validation service\n    echo \"🔧 SERVICE TESTS:\\n\";\n    \n    $testAmount = 500;\n    $result = \\App\\Services\\DeliveryService::validateMinimumOrderAmount($testAmount);\n    echo \"   Rs {$testAmount} order: \" . ($result['valid'] ? 'VALID ✅' : 'INVALID ❌') . \"\\n\";\n    \n    $testAmount = 1500;\n    $result = \\App\\Services\\DeliveryService::validateMinimumOrderAmount($testAmount);\n    echo \"   Rs {$testAmount} order: \" . ($result['valid'] ? 'VALID ✅' : 'INVALID ❌') . \"\\n\\n\";\n    \n    // Check enhanced services\n    echo \"🚀 ENHANCED FEATURES:\\n\";\n    \n    $servicesExist = [\n        'DeliveryService::validateMinimumOrderAmount' => method_exists('\\App\\Services\\DeliveryService', 'validateMinimumOrderAmount'),\n        'DeliveryService::getMinOrderValidationSettings' => method_exists('\\App\\Services\\DeliveryService', 'getMinOrderValidationSettings'),\n        'OrderValidationService' => class_exists('\\App\\Services\\OrderValidationService'),\n        'Client-side JS' => file_exists(__DIR__ . '/public/js/order-validation.js'),\n        'Validation CSS' => file_exists(__DIR__ . '/public/css/order-validation.css')\n    ];\n    \n    foreach ($servicesExist as $feature => $exists) {\n        echo \"   {$feature}: \" . ($exists ? 'AVAILABLE ✅' : 'MISSING ❌') . \"\\n\";\n    }\n    \n    echo \"\\n\" . str_repeat('-', 50) . \"\\n\";\n    \n    if ($enabled) {\n        echo \"✅ SYSTEM STATUS: ACTIVE AND WORKING\\n\";\n        echo \"\\n💡 HOW IT WORKS:\\n\";\n        echo \"   • Online orders below Rs {$amount} will be rejected\\n\";\n        echo \"   • POS sales are not affected\\n\";\n        echo \"   • Admin can change settings anytime\\n\";\n        echo \"   • Real-time validation on checkout page\\n\";\n    } else {\n        echo \"⚠️  SYSTEM STATUS: VALIDATION DISABLED\\n\";\n        echo \"\\n🔧 TO ENABLE:\\n\";\n        echo \"   1. Go to Admin Panel > Settings > Delivery Settings\\n\";\n        echo \"   2. Find 'Minimum Order Amount Validation' section\\n\";\n        echo \"   3. Check 'Enable Minimum Order Amount Validation'\\n\";\n        echo \"   4. Set your desired minimum amount\\n\";\n        echo \"   5. Save settings\\n\";\n    }\n    \n    echo \"\\n📍 ADMIN SETTINGS LOCATION:\\n\";\n    echo \"   Admin Panel > Settings > Delivery Settings\\n\";\n    echo \"   Section: 'Minimum Order Amount Validation'\\n\";\n    \n} catch (Exception $e) {\n    echo \"❌ ERROR: \" . $e->getMessage() . \"\\n\";\n    echo \"\\n🔧 SOLUTION:\\n\";\n    echo \"   Run: activate-enhanced-validation.bat\\n\";\n}\n\necho \"\\n\" . str_repeat('=', 50) . \"\\n\";\n"