@props(['paginator', 'label' => 'results'])\n\n@if ($paginator->hasPages())\n    <div class=\"pagination-container\">\n        <div class=\"pagination-info\">\n            <p class=\"small text-muted mb-0\">\n                Showing <span class=\"fw-semibold text-primary\">{{ $paginator->firstItem() }}</span>\n                to <span class=\"fw-semibold text-primary\">{{ $paginator->lastItem() }}</span>\n                of <span class=\"fw-semibold text-primary\">{{ $paginator->total() }}</span>\n                {{ $label }}\n            </p>\n        </div>\n        <div class=\"pagination-nav\">\n            @if(request()->query())\n                {{ $paginator->withQueryString()->links() }}\n            @else\n                {{ $paginator->links() }}\n            @endif\n        </div>\n    </div>\n@endif"